	public int maxProduct(final int[] A) {
        
        int res=A[0];
        
        
        int curMax=A[0];
        int curMin=A[0];
        
         for(int i=1;i<A.length;i++) {
             
        	 if(A[i]<0) {
        		 int temp=curMax;
        		 curMax=curMin;
        		 curMin=temp;
        	 } 
        	 
        	 curMax=Math.max(A[i], A[i]*curMax);
        	 curMin=Math.min(A[i], A[i]*curMin);
        	 
        	 if(curMax>res) {
        		 res=curMax;
        	 }
                
             
         }
         return res;
    }

